INSERT INTO `DBManagement_answer` (`id`,`question_id_id`,`content`) VALUES (2,'199500101002','$$y=6-(x-2)^4$$;$$\\frac{\\mathrm{d} y}{\\mathrm{d} x} = -4(x-2)^3$$;When $$x = 1$$, ;$$y = 6-(1-2)^4=5$$;$$\\frac{\\mathrm{d} y}{\\mathrm{d} x} = -4(1-2)^3=4$$;Gradient at normal at (1, 5) = $$-\\frac{1}{4}$$;Equation of normal at (1, 5): ;$$ \\frac{(y-5)}{(x-1)}= -\\frac{1}{4}$$;$$4y-20 = -x+1$$;$$4y+x = 21$$;');
INSERT INTO `DBManagement_answer` (`id`,`question_id_id`,`content`) VALUES (3,'199500101003','$$y+4 = x$$ (1);$$y^2+17 = 2x^2$$ (2); From (1): $$y =x - 4$$;Substitute into (2):;$$(x-4)^2+17 = 2x^2$$; $$x^2-8x+16+17=2x^2$$;$$x^2+8x-33=0$$;$$(x+11)(x-3)=0$$;x = -11 or 3 respectively;y= -15 or -1 respectively;The solutions are (-11, -15) and (3, -1).');
INSERT INTO `DBManagement_answer` (`id`,`question_id_id`,`content`) VALUES (4,'199500101004','Coordinates of M = $$\\frac{(-1+3)}{2},\\frac{(10+2)}{2}= (1,6)$$; AM = $$\\sqrt{(5-1)^2+(8-6)^2}$$;= $$\\sqrt{20}$$ (ANS);Gradient of AM = $$\\frac{(8-6)}{(5-1)}= \\frac{2}{4}= \\frac{1}{2}$$;		       Gradient of AC = $$\\frac{(10-2)}{(-1-3)} = \\frac{8}{-4}= -2$$;since the products of the gradients is - 1, the lines are perpendicular,(shown);Area of triangle ABC = $$\\frac{1}{2} (AM)(BC)$$  = $$\\frac{1}{2} (\\sqrt {20})(\\sqrt{(-1-3)^2+(10-2)^2)}$$;= $$\\frac{1}{2} (\\sqrt {20})(\\sqrt{80})$$;= $$\\frac{1}{2} (\\sqrt {1600})$$;= $$\\frac{1}{2} (40)$$;= 20 (ANS)   ');
INSERT INTO `DBManagement_answer` (`id`,`question_id_id`,`content`) VALUES (5,'199500101005','$$(\\cot A-\\tan A)\\cos A$$= $$(\\frac{\\cos A}{\\sin A} - \\frac{\\sin A}{\\cos A})\\cos A$$;= $$\\frac{(\\cos ^2A)}{\\sin A} - \\sin A$$;= $$\\frac{(1-\\sin ^2A)}{(\\sin A)}-\\sin A$$;= $$\\csc A - \\sin A - \\sin A$$;= $$\\csc A - 2\\sin A$$ (Proved)');
INSERT INTO `DBManagement_answer` (`id`,`question_id_id`,`content`) VALUES (6,'199500101006','a = coefficient of $$x^2$$;    = $$\\binom{20}{2}3^18$$;b = coefficient of $$x^3$$;   = $$\\binom{20}{2}3^17$$;  $$\\frac{a}{b} = (\\binom{20}{2}3^18) / (\\binom{20}{2}3^17) ;= \\frac{\\frac{(20!)}{(2!18!)(3)}}{\\frac{(20!)}{(17!(3)}};= 3(\\frac{(3!17!)}{(2!18!)})$$;= $$3(\\frac{3}{18})$$;= $$(\\frac{1}{2})$$');
