INSERT INTO `DBManagement_answer` (`id`,`question_id_id`,`content`) VALUES (481,'200400103005','$$(a+x)(1-2n)^n$$;=$$(a+x)[1^n-C_1^n(1)^{n-1}(2x)+C_2^n(1)^{n-2}(2x)^2-...]$$;=$$(a+x)[1-2nx+\\frac{ (n(n-1))}{2}(4x^2)-...]$$;=$$(a+x)[1-2nx+2n(n-1)x^2-...]$$;=$$a-2nax+2an(n-1)x^2+x-2nx^2+...$$;=$$a+(1-2an)x+2n[a(n-1)-1]x^2+...$$;$$a+(1-2an)x+2n[a(n-1)-1]x^2$$;=$$3-41x+bx^2$$;There a = 3;$$1 - 2an = - 41$$;$$1 - 6n = - 41$$;6n = 42;n = 7;b = 2n [a (n - 1) - 1];= 14 [3 (6) - 1];= 238 ;a=3,b=238, n=7 (ANS)');
